body.script:
// === ADVANCED DEDUPLICATION and TICKER SYSTEM ===
document.addEventListener('DOMContentLoaded', function() {

   // 1. Advanced Duplicate Removal (Hero vs Grid)
   // This function scans ALL grid items and removes any that match the Hero post.
   function removeDuplicates() {
       var heroLink = document.querySelector('.hero-post a');
       if (!heroLink) return;

       var heroUrl = heroLink.href;
       var gridCards = document.querySelectorAll('.post-card');

       gridCards.forEach(function(card) {
           var cardLink = card.querySelector('a');
           if (cardLink && cardLink.href === heroUrl) {
               card.remove(); // Completely remove the duplicate element from DOM
               console.log('Duplicate post removed:', heroUrl);
           }
       });
   }

   // Run immediately
   removeDuplicates();

   // 2. Live News Ticker (Iran Intl)
   // Uses CORS Proxy + Cache Busting for fresh news
   var rssUrl = 'https://www.iranintl.com/fa/rss';
   var timestamp = new Date().getTime();
   var proxyUrl = 'https://corsproxy.io/?' + encodeURIComponent(rssUrl) + '&_t=' + timestamp;

   fetch(proxyUrl)
    .then(function(res) { 
        if (!res.ok) throw new Error("Network response was not ok");
        return res.text(); 
    })
    .then(function(str) {
        var parser = new DOMParser();
        var xml = parser.parseFromString(str, "text/xml");
        var items = xml.getElementsByTagName("item");
        var headlines = [];

        // Extract top 10 headlines
        for(var i=0; i < Math.min(items.length, 10); i++) {
            var t = items[i].getElementsByTagName("title")[0];
            if(t) headlines.push(t.textContent);
        }

        // Display
        if(headlines.length > 0) {
           var ticker = document.getElementById('live-ticker');
           if(ticker) ticker.innerHTML = headlines.join(' \u2022 '); // Bullet separator
        } else {
           throw new Error("No news items found");
        }
    })
    .catch(function(e) {
        console.error('Ticker Error:', e);
        var ticker = document.getElementById('live-ticker');
        if(ticker) ticker.innerHTML = "پوشش لحظه‌ای مهم‌ترین اخبار ایران و جهان";
    });
});

// Clear Google Translate cookies on page load - Always start with Persian
(function() {
  document.cookie = 'googtrans=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;';
  document.cookie = 'googtrans=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/; domain=.blogspot.com;';
  document.cookie = 'googtrans=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/; domain=' + location.hostname + ';';
})();

function googleTranslateElementInit() {
  new google.translate.TranslateElement({
    pageLanguage: 'fa',
    includedLanguages: 'en,de',
    autoDisplay: false
  }, 'google_translate_element');
}

function translateTo(lang) {
  if (lang === 'fa') {
    // Reset to original - clear cookies and reload
    document.cookie = 'googtrans=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;';
    document.cookie = 'googtrans=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/; domain=.blogspot.com;';
    document.cookie = 'googtrans=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/; domain=' + location.hostname + ';';
    location.reload();
    return;
  }

  var select = document.querySelector('.goog-te-combo');
  if (select) {
    select.value = lang;
    select.dispatchEvent(new Event('change'));
  } else {
    // Fallback: Try again after widget loads
    setTimeout(function() {
      var s = document.querySelector('.goog-te-combo');
      if (s) { s.value = lang; s.dispatchEvent(new Event('change')); }
    }, 1000);
  }
}

// Standard Infinite Scroll Logic
(function() {
  var loading = false;
  // Note: Selector updated for new layout structure
  var container = document.querySelector('.homepage-loop'); 
  var nextLink = document.getElementById('blog-pager-older-link');
  var nextPageUrl = nextLink ? nextLink.href : null;

  if(container && nextLink) {
    window.addEventListener('scroll', function() {
      if(loading || !nextPageUrl) return;
      if((window.innerHeight + window.scrollY) >= document.body.offsetHeight - 800) {
        loading = true;
        fetch(nextPageUrl).then(r => r.text()).then(html => {
           var doc = new DOMParser().parseFromString(html, 'text/html');
           var newPosts = doc.querySelectorAll('.post-card');
           newPosts.forEach(p => container.appendChild(p));

           var newNext = doc.getElementById('blog-pager-older-link');
           if(newNext) nextPageUrl = newNext.href; else nextPageUrl = null;
           loading = false;
        });
      }
    });
  }
})();
